import { useState } from "react";

const CreateTask = () => {

    const [task, setTask] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();
    }


    const onChange = (e) => {
        setTask(e.target.value);
        console.log(task);
    }

    return (

        <form onSubmit={onSubmit}>
            <input
                type="text"
                name="taskName"
                placeholder="Do the dishes"
                value={task}
                onChange={onChange}
            />
            <input type="submit" value="Add" />
        </form>
    );
}

export default CreateTask;
