import { useEffect, useState } from "react";
const url ='http://localhost:3030/jsonstore/todos';
const useTodosApi = () => {


    const removeTodoApi = (todoId) => {
        fetch(`${url}/${todoId}`, {
            method: 'DELETE',
        }).then(res => res.json())
    }

    return {
        remove: useTodosApi
    };
}

export default useTodos;
