//import { useEffect, useState } from "react";
const url ='http://localhost:3030/jsonstore/todos';
const useTodosApi = () => {


    const removeTodo = (todoId) => {
       return fetch(`${url}/${todoId}`, {
            method: 'DELETE',
        }).then(res => res.json())
    }

    return {
        emoveTodo
    };
}

export default useTodosApi;
